ta   path+2         lda   #$8000         sta   count         lda   #^$8000         sta   count+2         lda   #DrawPage         sta   buf         lda   #^DrawPage         sta   buf+2         jsr   ReadFile         rtsStartupScreenPath str 'startup.screen'PlayScreenPath str 'play.screen'         endUpdateTerrainScreen start;copy terrain screen from draw page to screen fast         using DrawPage         phb                            save direct page         pea   $E1E1                    move direct page to screen         plb         plb         ldx   #TerrainScreenXloop     anop         jsr   update         inx         inx         cpx   #TerrainScreenX+TerrainScreenWidth         bne   loop         plb                            restore direct page         rtsupdate   anop;        lda   >DrawPage+$0000,x;        sta   $2000,x;        lda   >DrawPage+$00A0,x;        sta   $20A0,x;        lda   >DrawPage+$0140,x;        sta   $2140,x;        lda   >DrawPage+$01E0,x;        sta   $21E0,x         lda   >DrawPage+$0280,x         sta   $2280,x         lda   >DrawPage+$0320,x         sta   $2320,x         lda   >DrawPage+$03C0,x         sta   $23C0,x         lda   >DrawPage+$0460,x         sta   $2460,x         lda   >DrawPage+$0500,x         sta   $2500,x         lda   >DrawPage+$05A0,x         sta   $25A0,x         lda   >DrawPage+$0640,x         sta   $2640,x         lda   >DrawPage+$06E0,x         sta   $26E0,x         lda   >DrawPage+$0780,x         sta   $2780,x         lda   >DrawPage+$0820,x         sta   $2820,x         lda   >DrawPage+$08C0,x         sta   $28C0,x         lda   >DrawPage+$0960,x         sta   $2960,x         lda   >DrawPage+$0A00,x         sta   $2A00,x         lda   >DrawPage+$0AA0,x         sta   $2AA0,x         lda   >DrawPage+$0B40,x         sta   $2B40,x         lda   >DrawPage+$0BE0,x         sta   $2BE0,x         lda   >DrawPage+$0C80,x         sta   $2